<script>
import swal from 'sweetalert'
export default {
    name: 'app',
    methods: {
        makan() {
            swal({
                icon: 'warning',
                title: 'Mau Makan?',
                buttons: ['Engga', 'Iya Mau'],
                dangerMode: true
            }).then(
                (makan) => {
                    if (makan) {
                        swal({
                            icon: 'error',
                            title: 'Sabar Belum Jadi!',
                            button: 'OKE'
                        })
                    } else {
                        swal({
                            title: 'Ya udah'
                        })
                    }
                }
            )
        }
    }
}
</script>