<script>
import swal from 'sweetalert'
import deviceAlert from '@/components/deviceAlert.vue'
export default {
    name: 'app',
    components: {
        'device-alert': deviceAlert
    },
    data() {
        return {
            isBouncing: false,
            isTada: false,
        }
    },
    mounted() {
        // alert(window.innerWidth)
        this.isTada = true
        this.isBouncing = true
        setInterval(() => {
            this.addShakeClass()
        }, 2000)
        setInterval(() => {
            this.addBounceClass()
        }, 1000)
    },
    methods: {
        addShakeClass() {
            this.isTada = true
            setTimeout(() => {
                this.isTada = false
            }, 1900)
        },
        addBounceClass() {
            this.isBouncing = true
            setTimeout(() => {
                this.isBouncing = false
            }, 900)
        },
        makan() {
            swal({
                icon: 'warning',
                title: 'Mau Makan?',
                buttons: ['Engga', 'Iya Mau'],
                dangerMode: true
            }).then(
                (makan) => {
                    if (makan) {
                        this.$router.push('/home')
                    } else {
                        swal({
                            title: 'Ya udah'
                        })
                    }
                }
            )
        }
    }
}
</script>